export default function HomeHero(){
 return (
 <section className="relative overflow-hidden rounded-3xl border bg-black text-white">
 {/* Image */}
 <img src="/hero-944.png" alt="944 Trafik taxi in Copenhagen" className="w-full h-[320px] md:h-[420px] object-cover opacity-90"/>
 {/* Gradient overlay */}
 <div className="absolute inset-0 bg-gradient-to-tr from-black/60 via-black/30 to-transparent"/>
 {/* Content */}
 <div className="absolute inset-0 flex items-center">
 <div className="container-page">
 <div className="max-w-xl">
 <h1 className="text-4xl md:text-5xl font-extrabold tracking-tight">Your trusted taxi in Denmark</h1>
 <p className="mt-3 text-sm md:text-base text-gray-100/90">Book instant or scheduled rides across Copenhagen & Frederikssund with transparent pricing.</p>
 <div className="mt-5 flex gap-2">
 <a href="/book" className="btn-primary">Book now</a>
 <a href="/account" className="btn-ghost text-white border-white/30 hover:bg-white/10">My account</a>
 </div>
 </div>
 </div>
 </div>
 </section>
 );
}

